$(document).ready(function () {
    $(document).on('submit','#yui11011001', function (e) {
        e.preventDefault();
        var x1=$('input[name=lab]').val();
        var x2=$('input[name=nom]').val();
        var x3=$('input[name=app]').val();
        var x4=$('input[name=apm]').val();
        var x5=$('input[name=mail]').val();
        var x6=$('input[name=add]').val();
        var x7=$('input[name=telo]').val();
        var x8=$('input[name=ext]').val();
        var x9=$('input[name=cel]').val();
        $.ajax({
            url: "./frmsbmt/clntintrg.php",
            type: 'POST',
            data: {
                x1:x1,x2:x2,x3:x3,x4:x4,x5:x5,x6:x6,x7:x7,x8:x8,x9:x9
            },
            dataType: 'json',
            encode: true
        }).done(function (v){
           console.log(v);
           $('#messagefrmrg').html(v.message);
           if(v.fail===0){setTimeout(location.reload(), 750);}
       });
    });
    $(document).on('click','.btn-info-uint',function(e){
        e.preventDefault();
        var d=$(this).attr('data-id');
        $('#info-uint').html('');
        $.ajax({
            url: "./frmsbmt/clntintinf.php",
            type: 'POST',
            data: {i:d},
            dataType: 'json',encode:true
        }).done(function (v){
           console.log(v);
           $('#info-uint').html('');$('#info-uint').html(v.message);
        }).fail(function(){
            $('#info-uint').html("<div class='alert alert-danger'>");
            $('#info-uint > .alert-danger').html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>")
            .append("</button>");
            $('#info-uint > .alert-danger').append("<strong>Fallo! </strong>Ocurrio un error al procesar la solicitud intente de nuevo mas tarde.");
            $('#info-uint > .alert-danger').append('</div>');
        });
    });
});